const assert = require("assert");
function cancelChatRequest(db, post, callback)
{
    db.collection('chatRequests').update
    (
        { 'coach': post['coach']}, 
        {$pull: { 'chatRequests': {'UUID':post["UUID"] } } },
        function(err,result){
            if(err)
            {
                return callback("error");
            }
            return callback("success");
        }
    );
}

module.exports = cancelChatRequest;