const assert = require('assert');
 
function updateMessageHasBeenSeen(db, post, callback)
{
    var finder={};
    
    finder['userID']=post['userID'];
    db.collection('contact_info').findOne(finder,function(err,result)
    {
        assert.equal(err,null);
        console.log(result)
        if(result==null)
        {
            db.close();
            return callback("no contact_info found "+post['userID']);
        }
        else if(typeof result=='undefined')
        {
            db.close();
            return callback("no contact_info found "+post['userID']);
        }
        else
        {
            db.close();
            return callback(JSON.stringify(result));
        }
    });
}

module.exports = updateMessageHasBeenSeen;
