const assert = require('assert'); 
    
function save(db,post, callback)
{
    db.collection('user').save(post["user"], {
        w: 1
    }, function(err, result) {
         assert.equal(err,null);
        db.close();
    });
    return callback("done");
                    
    
                
}
module.exports= save