const assert = require('assert');
 
function saveContactInfo(db, post, callback)
{
    db.collection('contact_info').save(post["contact_info"], {
        w: 1
    }, function(err, result) {
        assert.equal(err,null);
        db.close();
    });
    return callback("done");
}

module.exports = saveContactInfo;
