const assert = require('assert');
 
function updateMessageHasBeenSeen(db, post, callback)
{
    console.log(post);
    db.collection('message').findOneAndUpdate(
        {
          UUID: post["UUID"] 
          
        },
        { $set:
          {
            hasBeenSeen:'true'
          }
        },{
          returnNewDocument:true,
          returnOriginal:false
        },function(err,doc)
          {
            assert.equal(err,null);
          }
    )
    return callback("updated");
}

module.exports = updateMessageHasBeenSeen;
